import React from "react";

import {
  Grid,
  Card,
  Typography,
  Button,
  CardActions,
  Box,
} from "@mui/material";
// import img1 from "../../assets/s2.jpg";
import "./category.style.scss";
const Category = ({ item }) => {
  return (
    <section sx={{ flexGrow: 1, display: "flex" }}>
      {/* // <Box sx={{ flexGrow: 1 }} className="categoryContainer"> */}
      <Grid container spacing={1}>
        <Grid item xs={12} sm={6} md={3} lg={3} mb={2}>
          <Card
            className="product-card"
            sx={{
              height: 400,
              width: "100%",
              backgroundImage: `url(${item.img})`,
              backgroundSize: "cover",
              backgroundPosition: "center",
              position: "relative",
            }}
          >
            {/* <div class="product-image">
                <img src={item.img} alt="Product Image" />
              </div> */}
            <CardActions
              className="cardContent"
              sx={{
                position: "absolute",
                bottom: 0,
                width: "100%",
                padding: "16px",
              }}
            >
              <Box
                sx={{
                  flexGrow: 1,
                  textAlign: "center",
                  padding: "8px",
                }}
              >
                <Typography variant="h5" className="product-title">
                  {" "}
                </Typography>

                <Button variant="outlined" className="btnBuy">
                  Buy Now
                </Button>
              </Box>
            </CardActions>
          </Card>
        </Grid>
      </Grid>
    </section>
  );
};

export default Category;
